package in.shabhushan.cp_trials.string;

import org.junit.Test;

import static in.shabhushan.cp_trials.string.LongestCommonSubsequence.longestCommonSubsequence;
import static in.shabhushan.cp_trials.string.LongestCommonSubsequence.longestCommonSubsequence2;
import static org.junit.Assert.assertEquals;

public class LongestCommonSubsequenceTest {
  @Test
  public void testLongestCommonSubsequence() {
    assertEquals(3, longestCommonSubsequence("abcde", "ace"));
    assertEquals(3, longestCommonSubsequence("abc", "abc"));
    assertEquals(4, longestCommonSubsequence("pmjghexybyrgzczy", "hafcdqbgncrcbihkd"));
    //assertEquals(4, longestCommonSubsequence("", ""));
  }

  @Test
  public void testLongestCommonSubsequence2() {
    assertEquals(2, longestCommonSubsequence2("bd", "abcd"));
    assertEquals(3, longestCommonSubsequence2("abcde", "ace"));
    assertEquals(3, longestCommonSubsequence2("abc", "abc"));
    assertEquals(4, longestCommonSubsequence2("pmjghexybyrgzczy", "hafcdqbgncrcbihkd"));
    assertEquals(323, longestCommonSubsequence2("", ""));
  }
}
