package in.shabhushan.cp_trials.string;

import org.junit.Test;

import java.util.stream.IntStream;

import static in.shabhushan.cp_trials.string.ValidateCC.*;
import static org.junit.Assert.assertEquals;

public class ValidateCCTest {
    @Test
    public void test891(){
        IntStream.of(0, 100000).forEach(integer -> {
                assertEquals(false, validate("891"));
                assertEquals(false, validate(""));
            }
        );
    }

    @Test
    public void test2(){
        IntStream.of(0, 100000).forEach(integer -> {
                assertEquals(false, validate2("891"));
                assertEquals(false, validate2(""));
            }
        );
    }
}
